package week1.day2;

public class Rama {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark=75;
		if(mark==35) {
			System.out.println("Grade E");
		}
		else if(mark>=50) {
			System.out.println("Grade D");	
		}
		else if(mark>65) {
			System.out.println("Grade C");
		}
		else if(mark>=75) {
			System.out.println("Grade B");
		}
		else {
			System.out.println("Grade A");
		}

	}

}
